import React from 'react';
import '../style/style.css';

const Select = (props) => {
    const inputStyleObj = {
        background:'none',
	    backgroundColor:'#fbfbfb',
        border:'2px solid #d5d5d5',
        borderRadius: '5px',
        boxShadow: 'none',
  	    // color: '#999',
	    height:'30px',
	    fontSize:'16px',
	    fontWeight:'400',
	    fontFamily: 'Open Sans, Arial, sans-serif',
	    lineHeight: '1.428571429',
        padding: '5px 10px',
        verticalAlign: 'middle'
    }
    return (
        <div style={{display:'flex',alignItems:'center', padding:'5px', marginLeft: '50px'}}>
            <h5 style={{padding:'5px', paddingLeft:'60px'}}>Please select a State:</h5>
            <div style={{paddingLeft: '20px'}}>
                <select style={inputStyleObj} onChange={props.getCurrState()}>
                    <option val="AZ">Arizona</option>
                </select>
            </div>
            <div style={{paddingLeft: '30px'}}>
                <button className="forward">Get Companies</button>
            </div>
        </div>
    )
}

export default Select;
