import React from 'react';
import {Container, Row, Col} from 'react-bootstrap';
import Image from 'react-image-resizer';
var imageCheck = require('../images/check-icon-vector-10850982.jpg');
var companyImage;
const GridContainer = (props) => {
        const availableProducts = ['Personal Auto', 'Personal Home', 'Dwell fire', 'Commercial BOP', 'Commercial BAP', 'Commercial WC'];
        if(props.products.length > 0) {
            companyImage = require('../images'+props.products[0].Image)
        }
        return(
            <Container>
                <Row >
                    <Col xs={3} md={1}>Company</Col>
                    {availableProducts.map((elem) => {
                        return <Col xs={3} md={1}>{elem}</Col>
                    })}
                </Row>
                <br></br>
                <Row>
                    <Col xs={3} md={1} style={{justifyContent: 'center'}}>
                        <Image src={companyImage} height={60} width={60}/>
                    </Col>
                </Row>
            </Container>
        )
}

export default GridContainer;
